mapper.select:
SELECT *
FROM member


		MEMBER_ID LIKE #{member_id}


		AND PASSWORD LIKE #{password}

mapper.update:
	  UPDATE member SET

    		NICKNAME = #{nickname}


    		PASSWORD = #{password}


    		PHONE = #{phone}


    		PROFILEIMG = #{profileimg}


    		COMMENT = #{comment}

	  WHERE MEMBER_ID LIKE #{member_id}

mapper.delete:
DELETE FROM member
WHERE MEMBER_ID = #{member_id}
AND PASSWORD = #{password}

mapper.update:
  	  UPDATE RESERVATION R
	  	JOIN PAYMENT P
	  		ON R.PAY_NO = P.PAY_NO
	  SET  R.RES_CHECK = '2'
	  WHERE P.MEMBER_ID = #{member_id}
	  AND R.PAY_NO = #{pay_no};
